INSERT INTO tipps (tipp) VALUES ('MUSS DAS WIRKLICH IN DIE WÄSCHE? Sweater oder Pulli riechen nach einer Nacht auf dem Balkon wieder frisch Altes Brot als Pizzaboden verwenden');
INSERT INTO tipps (tipp) VALUES ('"Drive-In" im eigenen Garten Ein weißes Laken als Leinwand und den Beamer aus dem Büro ausleihen, um einen Filmabend unter freiem Himmel zu genießen. Spart Kinokosten und das Popcorn ist auch günstiger.');
INSERT INTO tipps (tipp) VALUES ('Katze als Staubwedel Mit einem freundlichen Streicheln über Möbel und Regale wird die Katze zum lebenden Staubwedel. Spart den Kauf von Staubtüchern und die Katze genießt die Aufmerksamkeit.');
INSERT INTO tipps (tipp) VALUES ('Doppelter Nutzen von Kondenswasser Das Kondenswasser der Klimaanlage oder des Wäschetrockners auffangen und für das Gießen der Pflanzen verwenden. Spart Wasser und ist umweltfreundlich.');
INSERT INTO tipps (tipp) VALUES ('Eierkartons als Schuhregal Alte Eierkartons bieten die perfekte Größe, um Schuhe ordentlich zu verstauen. Spart Geld für ein Schuhregal und recycelt gleichzeitig.');
INSERT INTO tipps (tipp) VALUES ('Alte CDs als Vogelscheuchen Die reflektierende Oberfläche alter CDs kann im Garten als Vogelscheuche dienen. Spart den Kauf einer Vogelscheuche und gibt den CDs eine neue Bestimmung.');
INSERT INTO tipps (tipp) VALUES ('Urlaubsfotos als Fensterdekoration Statt teurer Gardinen oder Vorhänge, einfach Urlaubsfotos vergrößern und ans Fenster hängen. Spart Geld und hält die Erinnerungen lebendig.');
